format:
/* General Page Styling */
body {
    font-family: Arial, sans-serif;
    background-color: #f4f4f4; /* Light background */
    color: black !important; /* Global black text */
    text-align: center;
    margin: 0;
    padding: 0;
}

/* Dashboard Container */
.dashboard-container {
    max-width: 1200px;
    margin: auto;
    padding: 20px;
    background: #ffffff; /* White background */
    border-radius: 10px;
    box-shadow: 2px 2px 12px rgba(0, 0, 0, 0.1);
    color: black !important;
}

/* LED Indicators */
.status {
    display: flex;
    justify-content: center;
    gap: 50px;
    margin: 20px 0;
}

.led {
    width: 80px;
    height: 80px;
    border-radius: 50%;
    border: 5px solid black;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: bold;
    font-size: 16px;
    text-transform: uppercase;
    color: black !important; /* Black text */
}

/* LED Colors */
.led.green {
    background-color: green;
}

.led.red {
    background-color: red;
}

/* Tank Status Styling */
.tank-status {
    display: flex;
    justify-content: space-around;
    margin: 20px 0;
}

.tank {
    width: 45%;
    padding: 10px;
    text-align: center;
    border: 2px solid #007BFF;
    border-radius: 10px;
    background: #f9f9f9;
    color: black !important;
    font-weight: normal;
    font-size: 18px;
}

/* Tank Level Bars */
.tank-bar {
    width: 100%;
    background-color: #ddd;
    height: 40px;
    border-radius: 10px;
    overflow: hidden;
    margin-top: 10px;
    position: relative;
}

/* Tank Level Indicator */
.tank-level {
    height: 100%;
    text-align: center;
    line-height: 40px;
    color: black !important; /* Black text */
    background-color: #cfcfcf !important; /* Light gray background */
    font-weight: normal !important;
    transition: width 0.5s ease-in-out;
    position: absolute;
    left: 0;
    top: 0;
}

/* Dropdown Container */
.dropdown-container {
    margin: 20px 0;
}

/* Updated Widget Label Styling */
label, 
.nr-dashboard-card-title, 
.nr-dashboard-gauge text, 
.nr-dashboard-chart text {
    font-size: 22px !important;
    font-weight: bold !important;
    color: black !important; /* Black text */
}

/* Dropdown Styling */
.select-container {
    position: relative;
    display: inline-block;
}

select {
    padding: 10px;
    font-size: 16px;
    border-radius: 5px;
    border: 1px solid #007BFF;
    cursor: pointer;
    background-color: #ffffff;
    color: black !important;
    outline: none;
    transition: all 0.3s ease-in-out;
}

select:hover {
    border-color: #0056b3;
}

/* Button Styling */
.button-container {
    margin-top: 20px;
}

button {
    padding: 10px 15px;
    font-size: 16px;
    background-color: #007BFF;
    color: black !important;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    transition: background 0.3s ease-in-out;
}

button:hover {
    background-color: #0056b3;
}

/* Charts */
.chart-container {
    margin-top: 20px;
}

canvas {
    max-width: 100%;
    border-radius: 5px;
}

/* Chart Text in Black */
.nr-dashboard-chart text {
    fill: black !important; /* Ensures text inside charts is black */
}

/* Ensures all text in widgets is black */
.nr-dashboard-text, 
.nr-dashboard-gauge text, 
.nr-dashboard-chart text, 
.nr-dashboard-label, 
.nr-dashboard-table, 
.nr-dashboard-group {
    color: black !important;
}

