let a = 20;
flow.set('material_shortage', a); // store for reference

// Get previous endtime
let prevEndStr = flow.get('material_endtime');
let prevEnd;

if (prevEndStr) {
    prevEnd = new Date(prevEndStr);
    if (isNaN(prevEnd.getTime())) {
        node.warn("Invalid prev_endtime detected. Falling back.");
        prevEnd = new Date(Date.now() - a * 60000); // fallback
    }
} else {
    prevEnd = new Date(Date.now() - a * 60000); // fallback
}

// Current time
let currStart = new Date();
if (isNaN(currStart.getTime())) {
    node.error("Invalid current time");
    return null;
}

// Debug
node.warn(`prevEnd: ${prevEnd.toISOString()}, currStart: ${currStart.toISOString()}`);

// Incident/failure count
let incidentCount = flow.get('material_count') || 1;

//  MTBF Calculation
let diffMs = currStart.getTime() - prevEnd.getTime();
// let mtbf = Math.floor(diffMs / (60000 * incidentCount)); // MTBF in minutes

// End time (current start + duration 'a')
let endTime = new Date(currStart.getTime() + a * 60000);

// --- New Calculations for OEE Metrics ---

// Total accumulated uptime (for MTTF)
let totalUptime = flow.get('material_total_uptime') || 0;
// totalUptime += mtbf;

// Total accumulated repair time (for MTTR)
let repairTime = flow.get('material_last_repair_time') || 10; // assume fixed repair time 10 mins for now
let totalRepairTime = flow.get('material_total_repair') || 0;
totalRepairTime += repairTime;

let totalTime = totalUptime + totalRepairTime;


let mttf = Math.floor(totalTime / incidentCount);

// Update counts
flow.set('material_total_uptime', totalUptime);
flow.set('material_total_repair', totalRepairTime);

// MTTF = total operating time / number of failures
let mtbf = Math.floor(totalUptime / incidentCount);

// MTTR = total repair time / number of failures
let mttr = Math.floor(totalRepairTime / incidentCount);

// MTBSI and MTBM = MTBF + MTTR
// let mtbsi = (mtbf + mttr) / incidentCount;

let mtbsi = totalTime / incidentCount;

let mtbm = mtbf + mttr;



// Availability (%) = (MTBF / (MTBF + MTTR)) * 100
let availability = (mtbf / (mtbf + mttr)) * 100;
availability = Math.floor(availability);

// Failure Rate = 1 / MTTF
let failureRate = (mttf !== 0) ? (1 / mttf).toFixed(4) : 0;

// Placeholder for MTTA (time to acknowledge) and MTTD (time to detect)
let mtta = 5;  // assuming 5 minutes for MTTA
let mttd = 3;  // assuming 3 minutes for MTTD

// --- Save context for next calculation ---
flow.set('material_endtime', endTime.toISOString());
flow.set('material_count', incidentCount + 1);

// --- Final Insert Query ---
msg.topic = "INSERT INTO material_shortage (starttime, endtime, Incidents, Uptime, Downtime, MTBF, MTTR, MTTF, MTBSI, Availability, FailureRate, MTBM, MTTA, MTTD, TotalTime) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)";
msg.payload = [
    currStart.toISOString().slice(0, 19).replace('T', ' '),
    endTime.toISOString().slice(0, 19).replace('T', ' '),
    incidentCount,
    totalUptime,
    totalRepairTime,
    mtbf,
    mttr,
    mttf,
    mtbsi,
    availability,
    failureRate,
    mtbm,
    mtta,
    mttd,
    totalTime
];

return msg;

