function calculateQuartiles(arr) {
    arr.sort((a, b) => a - b);
    var q1Index = Math.floor(arr.length / 4);
    var q3Index = Math.ceil((3 * arr.length) / 4) - 1;

    var q1 = arr[q1Index];
    flow.set('Q1', q1);

    var q3 = arr[q3Index];
    flow.set('Q3', q3);

    return { q1, q3 };
}

function calculateSkewness(values, mean, stdDev) {
    let n = values.length;
    let skewSum = values.reduce((sum, val) => sum + Math.pow((val - mean) / stdDev, 3), 0);
    return n > 2 ? skewSum * (n / ((n - 1) * (n - 2))) : 0;
}

function calculateKurtosis(values, mean, stdDev) {
    let n = values.length;
    let kurtSum = values.reduce((sum, val) => sum + Math.pow((val - mean) / stdDev, 4), 0);
    return n > 3 ? ((n * (n + 1)) / ((n - 1) * (n - 2) * (n - 3))) * kurtSum - (3 * Math.pow(n - 1, 2) / ((n - 2) * (n - 3))) : 0;
}

