    var values = [];
    var timestamp = group[0]?.timestamp || null;

    group.forEach(d => {
        values.push(parseFloat(d.voltage));
    });

    if (values.length > 0) {
        var mean = values.reduce((a, b) => a + b, 0) / values.length;
        var stdDev = Math.sqrt(values.reduce((sum, val) => sum + Math.pow(val - mean, 2), 0) / values.length);

        var { q1, q3 } = calculateQuartiles(values);
        var iqr = q3 - q1;

        var lowerBound = q1 - 1.5 * iqr;
        var upperBound = q3 + 1.5 * iqr;

        var outliers = values.filter(v => v < lowerBound || v > upperBound);

        var skewness = calculateSkewness(values, mean, stdDev);
        flow.set('skewness', skewness.toFixed(3));

        var kurtosis = calculateKurtosis(values, mean, stdDev);
        flow.set('kurtosis', kurtosis.toFixed(3));

        let now = new Date();

// // Convert to IST (UTC +5:30)
       let istTime = new Date(now.getTime() + (5.5 * 60 * 60 * 1000)).toISOString().slice(0, 19).replace("T", " ");



        processedData.push({
            timestamp: istTime,
            Q1: q1.toFixed(2),
            Q3: q3.toFixed(2),
            IQR: iqr.toFixed(2),
            UpperBound: upperBound.toFixed(2),
            LowerBound: lowerBound.toFixed(2),
            // Outliers: outliers.map(v => v.toFixed(2)),
            Skewness: skewness.toFixed(3),
            Kurtosis: kurtosis.toFixed(3),
            // topic: "Quartile + Shape Analysis"
        });
    }
}

msg.topic = "Q1, Q3, IQR, Outliers, Skewness, Kurtosis";
msg.payload = processedData;
return msg;

// Fetch from flow context
var totalDefects = flow.get('totalDefects');
var totalSamples = flow.get('total_samples');

if (!totalDefects || !totalSamples || totalSamples === 0) {
    node.warn("Invalid defect or sample data.");
    return null;
}

//  Step 1: Calculate Defect Rate
var defectRate = totalDefects / totalSamples;

//  Step 2: Two-Tail Process
var twoTailProcess = defectRate / 2;

//  Step 3: Inverse Standard Normal (Z-score)
function normSInv(p) {
    // Abramowitz and Stegun approximation
    if (p <= 0 || p >= 1) {
        throw new Error("Invalid input for normSInv: p must be between 0 and 1");
    }

    const a1 = -39.69683028665376, a2 = 220.9460984245205, a3 = -275.9285104469687;
    const a4 = 138.3577518672690, a5 = -30.66479806614716, a6 = 2.506628277459239;
    const b1 = -54.47609879822406, b2 = 161.5858368580409, b3 = -155.6989798598866;
    const b4 = 66.80131188771972, b5 = -13.28068155288572;
    const c1 = -0.007784894002430293, c2 = -0.3223964580411365;
    const c3 = -2.400758277161838, c4 = -2.549732539343734;
    const c5 = 4.374664141464968, c6 = 2.938163982698783;
    const d1 = 0.007784695709041462, d2 = 0.3224671290700398;
    const d3 = 2.445134137142996, d4 = 3.754408661907416;

    const plow = 0.02425;
    const phigh = 1 - plow;
    let q, r;

    if (p < plow) {
        q = Math.sqrt(-2 * Math.log(p));
        return (((((c1 * q + c2) * q + c3) * q + c4) * q + c5) * q + c6) /
               ((((d1 * q + d2) * q + d3) * q + d4) * q + 1);
    }

    if (p > phigh) {
        q = Math.sqrt(-2 * Math.log(1 - p));
        return -(((((c1 * q + c2) * q + c3) * q + c4) * q + c5) * q + c6) /
                ((((d1 * q + d2) * q + d3) * q + d4) * q + 1);
    }

    q = p - 0.5;
    r = q * q;
    return (((((a1 * r + a2) * r + a3) * r + a4) * r + a5) * r + a6) * q /
           (((((b1 * r + b2) * r + b3) * r + b4) * r + b5) * r + 1);
}

// Compute Z-score
var zScore = normSInv(1 - twoTailProcess);
flow.set('zScore', zScore.toFixed(4));

//  Output
msg.payload = {
    totalDefects: totalDefects,
    totalSamples: totalSamples,
    defectRate: defectRate.toFixed(6),
    twoTailProcess: twoTailProcess.toFixed(6),
    zScore: zScore.toFixed(4)
};

