var mean = flow.get('overall_average');

if (!ucl || !lcl || !stdDev || stdDev === 0 || !mean) {
    node.warn("Missing or invalid UCL, LCL, mean, or stdDev.");
    return null;
}

//  Standard Normal CDF approximation (NORM.S.DIST)
function normSDist(z) {
    return 0.5 * (1 + erf(z / Math.sqrt(2)));
}

//  Error Function approximation (used in CDF)
function erf(x) {
    // Abramowitz and Stegun formula 7.1.26
    var sign = (x >= 0) ? 1 : -1;
    x = Math.abs(x);

    var a1 = 0.254829592,
        a2 = -0.284496736,
        a3 = 1.421413741,
        a4 = -1.453152027,
        a5 = 1.061405429,
        p = 0.3275911;

    var t = 1.0 / (1.0 + p * x);
    var y = 1.0 - (((((a5 * t + a4) * t + a3) * t + a2) * t + a1) * t) * Math.exp(-x * x);

    return sign * y;
}

//  Z-scores
var Zucl = parseFloat((ucl - mean) / stdDev);
var Zlcl = parseFloat((lcl - mean) / stdDev);

//  Tail probabilities
var pUCL = 1 - normSDist(Zucl);

var pLCL = normSDist(Zlcl);

var total = pUCL + pLCL;

//  Store in flow
flow.set('Zucl', Zucl);
flow.set('Zlcl', Zlcl);
flow.set('pUCL', pUCL);
flow.set('pLCL', pLCL);

//  Output
